import React, { Component } from 'react'
import Box from './Box'
import List from './List'

export default class Facade extends Component {
    constructor(props) {
        super()
        this.state = {
            item: [],
            currenItem: {
                text: '',
                key: ''
            }
        }
    }

    handleInput = e => {
        const txtIn = e.target.value
        const currenItem = {
            text: txtIn,
            key: Date.now()
        }
        console.log(currenItem)
        this.setState({currenItem: currenItem})
    }

    add = e => {
        e.preventDefault()
        const newItem = this.state.currenItem
        console.log(newItem)
        if (newItem.text !== '') {
            const item = [...this.state.item, newItem]
            this.setState({
                item: item,
                currenItem: {
                    text: '',
                    key: ''
                }
            })
        }
    }

    render() {
        return (
            <div className="App">
                <header className="App-header">
                    <p>Todo App</p>
                    <Box
                        add={this.add}
                        inputElement={this.inputElement}
                        handleInput={this.handleInput}
                        currenItem={this.state.currenItem}/>
                    <List
                        entries={this.state.item}/>
                </header>
            </div>
        )
    }
}
