import React, { Component } from 'react'
import Box from './Box'
import List from './List'
import axios from 'axios'

export default class Facade extends Component {
    constructor(props) {
        super()
        this.state = {
            item: [],
            currenItem: {
                text: '',
                key: ''
            }
        }
        this.update()
    }

    handleInput = e => {
        const txtIn = e.target.value
        const currenItem = {
            text: txtIn,
            key: Date.now()
        }
        console.log(currenItem)
        this.setState({currenItem: currenItem})
    }

    add = e => {
        e.preventDefault()
        const newItem = this.state.currenItem
        console.log(newItem)
        if (newItem.text !== '') {
            const item = [...this.state.item, newItem]
            this.setState({
                item: item,
                currenItem: {
                    text: '',
                    key: ''
                }
            })
            // console.log(this.state)
            // axios.post('http://localhost:9000/test', item).catch(err => {
            //     console.error(err)
            // })
        }
    }

    delete = (key) => {
        const filteredItem = this.state.item.filter(item => {
            return item.key !== key
        })
        this.setState({
            item: filteredItem
        })
    }

    update = () => {
        console.log('ma laew')
        fetch('http://localhost:9000/test', (res) => {
            const objStr = JSON.stringify(res.json())
            const dataJson = JSON.parse(objStr)
            console.log(dataJson)
        })
        // .then(a => a.json())
        // .then(a => JSON.stringify(a))
        // .then(a => JSON.parse(a))
        // .then(a => console.log(a.data.length))

        // for(var i = 0; i < a.data.length; i++){
        //     console.log(a.data[i])
        // }

        // .then(a => this.setState({item: b}))

        // .then(a => a.json())
            // .then(res => console.log(res))
            // .then(res => JSON.stringify(res))
            // .then(res => JSON.parse(res))
            // .then(res => console.log(res))
            // .then(res => this.setState({item: res}))
    }

    runUpdate = () => {
        this.update()
    }

    render() {
        return (
            <div className="App">
                <header className="App-header">
                    <p>Todo App</p>
                    <Box
                        add={this.add}
                        inputElement={this.inputElement}
                        handleInput={this.handleInput}
                        currentItem={this.state.currenItem}/>
                    <List
                        entries={this.state.item}
                        delete={this.delete}/>
                </header>
            </div>
        )
    }
}
